<?php 	include_once ("../../setup.php");		$page->privilege = 1;		$page->page_title = 'Script List';	$page->page_header = 'Script List';		if(isset($_POST['submit'])) {		$args['script'] = $_POST['scriptContent'];		$args['last_modifying_user'] = $_SESSION['username'];		$args['last_modified'] = date("Y-m-d H:i:s");		$where['id'] = $_POST['id'];		$database->update('scripts', $args, $where);				$page->html .= $alert->displaySuccess("The script has been edited!");	} else if(isset($_GET['id'])) {		$page->html .= '<p><strong>Viewing Script</strong> #'.$_GET['id'].'</p>';				$sql = "SELECT * FROM `scripts` WHERE id=".$_GET['id']."";		$script_result = $database->execute($sql);		$page->html .= '<p><strong>Script Airing Date:</strong> '.$script_result[0]['publish_date'].'</p>';				$page->html .= '<p><strong>Script:</strong></br></p>';				$page->html .= '<form name="editScript" action="edit_script.php" method="post">		<textarea rows="20" class="span9 input-xlarge required" id="scriptContent" name="scriptContent">'.$script_result[0]['script'].'</textarea>';				$page->html .= '<input type="hidden" name="id" value="'.$_GET['id'].'"></input><input type="submit" name="submit" class="btn btn-primary"></form>';	} else {		$page->html .= "<p>Script List:</p>";				$page->html .= '<table border="1">							<tr>								<th>Date</th>								<th>Script Page</th>							</tr>';							$sql = "SELECT `id`,`script`, `publish_date` FROM `scripts` ORDER BY `scripts`.`publish_date` DESC";							$script_result = $database->execute($sql);							foreach($script_result as $value) {								$page->html .= '<tr>								<td>'.date("l jS \of F Y",strtotime($value['publish_date'])) .'</td>								<td><a href="view_script.php?id='.$value['id'].'">View Script</a>';								if(isset($_SESSION['user_level']))									if($_SESSION['user_level'] > 0)										$page->html .= ' <a href="?id='.$value['id'].'">(Edit)</a>';																$page->html .= '</td></tr>';															}		$page->html .=				'</table>';	}	$page->display();?>