<?php 	include_once ("../../setup.php");		$page->page_title = 'Script List';	$page->page_header = 'Script List';	if(isset($_GET['id'])) {		$page->html .= '<p><strong>Viewing Script</strong> #'.$_GET['id'].'</p>';				$sql = "SELECT * FROM `scripts` WHERE id=".$_GET['id']."";		$script_result = $database->execute($sql);		$page->html .= '<p><strong>Script Airing Date:</strong> '.$script_result[0]['publish_date'].'</p>';				$page->html .= '<p><strong>Script:</strong></br></p>';				$page->html .= '<textarea id="userContent">'.$script_result[0]['script'].'</textarea>';			} else {		$page->html .= "<p>Script List:</p>";				$page->html .= '<table border="1">							<tr>								<th>Date</th>								<th>Script Page</th>							</tr>';							$sql = "SELECT `id`,`script`, `publish_date` FROM `scripts` ORDER BY `scripts`.`publish_date` DESC";							$script_result = $database->execute($sql);							foreach($script_result as $value) {								$page->html .= '<tr>								<td>'.date("l jS \of F Y",strtotime($value['publish_date'])) .'</td>								<td><a href="?id='.$value['id'].'">View Script</a>';								if(isset($_SESSION['user_level']))									if($_SESSION['user_level'] > 0)										$page->html .= ' <a href="edit_script.php?id='.$value['id'].'">(Edit)</a>';																$page->html .= '</td></tr>';															}		$page->html .=				'</table>';	}	$page->display();?>